class CricketPlayer {
    String name;
    int no_of_innings;
    int no_of_times_notout;
    int totalRuns;

    public CricketPlayer(String name, int no_of_innings, int no_of_times_notout, int totalRuns) {
        this.name = name;
        this.no_of_innings = no_of_innings;
        this.no_of_times_notout = no_of_times_notout;
        this.totalRuns = totalRuns;
    }

    public double battingAverage() {
        return (double) totalRuns / (no_of_innings - no_of_times_notout);
    }
}

public class Main {
    public static void main(String[] args) {
        CricketPlayer[] players = new CricketPlayer[3];
        players[0] = new CricketPlayer("Player 1", 10, 2, 500);
        players[1] = new CricketPlayer("Player 2", 8, 1, 400);
        players[2] = new CricketPlayer("Player 3", 12, 4, 600);

        java.util.Arrays.sort(players, (a, b) -> Double.compare(b.battingAverage(), a.battingAverage()));

        for (CricketPlayer player : players) {
            System.out.println(player.name + " - Average: " + player.battingAverage());
        }
    }
}

