class Bank {
    private double balance;

    public Bank() {
        this.balance = 0.0;
    }

    public void deposit(double amount) {
        if (amount > 0) {
            balance += amount;
            System.out.println("Deposited: " + amount);
        } else {
            System.out.println("Invalid deposit amount!");
        }
    }

    public void withdraw(double amount) {
        if (amount > 0 && amount <= balance) {
            balance -= amount;
            System.out.println("Withdrawn: " + amount);
        } else {
            System.out.println("Invalid withdrawal amount!");
        }
    }

    public void displayBalance() {
        System.out.println("Current Balance: " + balance);
    }

    public static void main(String[] args) {
        Bank bank = new Bank();
        bank.deposit(1000);
        bank.withdraw(500);
        bank.displayBalance();
    }
}

import java.io.*;
import java.util.*;

public class ReverseFileContents {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter the file path: ");
        String filePath = scanner.nextLine();

        try (BufferedReader br = new BufferedReader(new FileReader(filePath))) {
            List<String> lines = new ArrayList<>();
            String line;

            while ((line = br.readLine()) != null) {
                lines.add(line.toUpperCase());
            }

            Collections.reverse(lines);
            System.out.println("File Contents in Reverse Order:");
            for (String reversedLine : lines) {
                System.out.println(reversedLine);
            }
        } catch (IOException e) {
            System.out.println("Error reading file: " + e.getMessage());
        }
    }
}

class Sphere {
    private double radius;

    public Sphere(double radius) {
        this.radius = radius;
    }

    public double calculateVolume() {
        return (4.0 / 3.0) * Math.PI * Math.pow(radius, 3);
    }

    public double calculateSurfaceArea() {
        return 4 * Math.PI * Math.pow(radius, 2);
    }

    public static void main(String[] args) {
        Sphere sphere = new Sphere(5);
        System.out.println("Volume of Sphere: " + sphere.calculateVolume());
        System.out.println("Surface Area of Sphere: " + sphere.calculateSurfaceArea());
    }
}

