class StudentPer {
    public double calculatePercentage(int[] marks) {
        double total = 0;
        for (int mark : marks) {
            total += mark;
        }
        return total / marks.length;
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        StudentInfo student = new StudentInfo();
        student.acceptDetails();

        System.out.print("Enter marks of 6 subjects: ");
        int[] marks = new int[6];
        for (int i = 0; i < 6; i++) {
            marks[i] = scanner.nextInt();
        }

        StudentPer sp = new StudentPer();
        student.percentage = sp.calculatePercentage(marks);
        student.displayInfo();
    }
}

interface Operation {
