class Student {
    private String rollNumber;
    private String name;
    private SYMarks syMarks;
    private TYMarks tyMarks;

    public Student(String rollNumber, String name, SYMarks syMarks, TYMarks tyMarks) {
        this.rollNumber = rollNumber;
        this.name = name;
        this.syMarks = syMarks;
        this.tyMarks = tyMarks;
    }

    public void displayResult() {
        int totalMarks = syMarks.ComputerTotal + tyMarks.Theory;
        char grade;
        if (totalMarks >= 70) grade = 'A';
        else if (totalMarks >= 60) grade = 'B';
        else if (totalMarks >= 50) grade = 'C';
        else if (totalMarks >= 40) grade = 'D';
        else grade = 'F';

        System.out.println("Student: " + name + ", Roll No: " + rollNumber + ", Grade: " + grade);
    }
}

public class Main {
