import java.io.*;
import java.util.Scanner;

public class FileCopy {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter the source file name: ");
        String sourceFileName = scanner.nextLine();
        System.out.print("Enter the destination file name: ");
        String destinationFileName = scanner.nextLine();

        try (BufferedReader br = new BufferedReader(new FileReader(sourceFileName));
             BufferedWriter bw = new BufferedWriter(new FileWriter(destinationFileName))) {
            String line;
            while ((line = br.readLine()) != null) {
                bw.write(line);
                bw.newLine();
            }
            System.out.println("Contents copied successfully.");
        } catch (IOException e) {
            System.out.println("Error: " + e.getMessage());
        }
    }
}

