public class StringManipulator {
    public static void main(String[] args) {
        JFrame frame = new JFrame("String Manipulator");
        JTextField text1 = new JTextField(15);
        JTextField text2 = new JTextField(15);
        JTextField result = new JTextField(15);
        JButton concatButton = new JButton("Concatenate");
        JButton reverseButton = new JButton("Reverse");

        concatButton.addActionListener(new ActionListener() {
            public void actionPerformed(ActionEvent e) {
                String concatenated = text1.getText() + text2.getText();
                result.setText(concatenated);
            }
        });

        reverseButton.addActionListener(new ActionListener() {
            public void actionPerformed(ActionEvent e) {
                String reversed = new StringBuilder(text1.getText()).reverse().toString();
                result.setText(reversed);
            }
        });

        JPanel panel = new JPanel();
        panel.add(text1);
        panel.add(text2);
        panel.add(concatButton);
        panel.add(reverseButton);
        panel.add(result);

        frame.add(panel);
        frame.setSize(400, 200);
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.setVisible(true);
    }
}

