class CricketPlayer {
    String name;
    int noOfInnings;
    int noOfTimesNotOut;
    int totalRuns;

    public CricketPlayer(String name, int noOfInnings, int noOfTimesNotOut, int totalRuns) {
        this.name = name;
        this.noOfInnings = noOfInnings;
        this.noOfTimesNotOut = noOfTimesNotOut;
        this.totalRuns = totalRuns;
    }

    public double avg() {
        return (double) totalRuns / noOfInnings;
    }

    public String toString() {
        return "Name: " + name + ", Innings: " + noOfInnings + ", Not Out: " + noOfTimesNotOut + ", Total Runs: " + totalRuns + ", Average: " + avg();
    }
}

public class CricketPlayerDemo {
    public static void sortByAvg(CricketPlayer[] players) {
        for (int i = 0; i < players.length - 1; i++) {
            for (int j = i + 1; j < players.length; j++) {
                if (players[i].avg() < players[j].avg()) {
                    CricketPlayer temp = players[i];
                    players[i] = players[j];
                    players[j] = temp;
                }
            }
        }
    }

    public static void main(String[] args) {
        CricketPlayer[] players = new CricketPlayer[3];
        players[0] = new CricketPlayer("Player1", 10, 3, 450);
        players[1] = new CricketPlayer("Player2", 12, 4, 600);
        players[2] = new CricketPlayer("Player3", 8, 2, 250);

        sortByAvg(players);

        System.out.println("Player Details in Sorted Order:");
        for (CricketPlayer player : players) {
            System.out.println(player);
        }
    }
}

import java.util.Scanner;

public class DiagonalSum {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Enter the size of the matrix (n x n): ");
        int n = scanner.nextInt();
        int[][] matrix = new int[n][n];

        System.out.println("Enter the elements of the matrix:");
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                matrix[i][j] = scanner.nextInt();
            }
        }

        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += matrix[i][i]; // Sum of diagonal elements
        }

        System.out.println("Sum of diagonal elements: " + sum);
    }
}

