import java.util.Scanner;

public class CurrencyConverter {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter amount in Singapore Dollars (SGD):");
        double sgd = scanner.nextDouble();
        System.out.println("Enter amount in US Dollars (USD):");
        double usd = scanner.nextDouble();
        System.out.println("Enter amount in Euros:");
        double euro = scanner.nextDouble();

        double convertedToUSDFromSGD = sgd / 1.41;
        double convertedToEuroFromUSD = usd * 0.92;
        double convertedToSGDFromEuro = euro / 0.65;

        System.out.printf("%.2f SGD = %.2f USD\n", sgd, convertedToUSDFromSGD);
        System.out.printf("%.2f USD = %.2f Euro\n", usd, convertedToEuroFromUSD);
        System.out.printf("%.2f Euro = %.2f SGD\n", euro, convertedToSGDFromEuro);

        scanner.close();
    }
}

