import java.awt.*;
import java.awt.event.*;

public class ConcentricCircles extends Frame {
    public ConcentricCircles() {
        setTitle("Concentric Circles");
        setSize(400, 400);
        setVisible(true);

        addMouseListener(new MouseAdapter() {
            public void mouseClicked(MouseEvent e) {
                Graphics g = getGraphics();
                int x = e.getX();
                int y = e.getY();
                drawCircles(g, x, y);
            }
        });

        addWindowListener(new WindowAdapter() {
            public void windowClosing(WindowEvent we) {
                System.exit(0);
            }
        });
    }

    private void drawCircles(Graphics g, int x, int y) {
        for (int radius = 10; radius <= 30; radius += 10) {
            g.drawOval(x - radius, y - radius, 2 * radius, 2 * radius);
        }
    }

    public static void main(String[] args) {
        new ConcentricCircles();
    }
}

