public class DonorManagement {
    public static void main(String[] args) throws IOException, ClassNotFoundException {
        List<Donor> donors = new ArrayList<>();
        donors.add(new Donor("John Doe", 30, "123 Main St", "1234567890", "A+ve", new SimpleDateFormat("dd/MM/yyyy").parse("01/01/2024")));
        donors.add(new Donor("Jane Smith", 25, "456 Elm St", "0987654321", "O-ve", new SimpleDateFormat("dd/MM/yyyy").parse("15/05/2024")));

        // Write to file
        ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream("donors.dat"));
        oos.writeObject(donors);
        oos.close();

        // Read from file and display A+ve donors not donated in last 6 months
        ObjectInputStream ois = new ObjectInputStream(new FileInputStream("donors.dat"));
        List<Donor> readDonors = (List<Donor>) ois.readObject();
        ois.close();

        Calendar calendar = Calendar.getInstance();
        calendar.add(Calendar.MONTH, -6);
        Date sixMonthsAgo = calendar.getTime();

        System.out.println("Donors with Blood Group A+ve who haven't donated in the last 6 months:");
        for (Donor donor : readDonors) {
            if (donor.getBloodGroup().equals("A+ve") && donor.getLastDonationDate().before(sixMonthsAgo)) {
                donor.display();
            }
        }
    }
}

