import java.io.*;
import java.util.Scanner;

public class FileDirectoryCheck {
    public static void main(String[] args) {
        if (args.length != 1) {
            System.out.println("Please provide a file or directory path as command line argument.");
            return;
        }

        File file = new File(args[0]);

        if (file.isDirectory()) {
            File[] files = file.listFiles();
            int deletedCount = 0;
            Scanner scanner = new Scanner(System.in);

            System.out.println("Are you sure you want to delete all text files in this directory? (yes/no)");
            String response = scanner.nextLine();
            if (response.equalsIgnoreCase("yes")) {
                for (File f : files) {
                    if (f.isFile() && f.getName().endsWith(".txt")) {
                        if (f.delete()) {
                            deletedCount++;
                        }
                    }
                }
            }
            System.out.println(deletedCount + " text files deleted.");
            scanner.close();
        } else if (file.isFile()) {
            System.out.println("File Details:");
            System.out.println("Name: " + file.getName());
            System.out.println("Path: " + file.getAbsolutePath());
            System.out.println("Readable: " + file.canRead());
            System.out.println("Writable: " + file.canWrite());
            System.out.println("Size: " + file.length() + " bytes");
        } else {
            System.out.println("The specified path is neither a file nor a directory.");
        }
    }
}

