#include <stdio.h>
#include <fcntl.h>
#include <unistd.h>
#include <sys/wait.h>
#include <string.h>

void count(char *fn, char op) {
    int fh = open(fn, O_RDONLY);
    int cc = 0, wc = 0, lc = 0;
    char c;

    if(fh == -1) {
        printf("File %s not found.\n", fn);
        return;
    }

    while(read(fh, &c, 1) > 0) {
        cc++;
        if(c == ' ' || c == '\n') 
          wc++;
        if(c == '\n') 
          lc++;
    }
    close(fh);

    if(cc > 0){
      wc++;
      lc++;
    }

    if(op == 'c') 
      printf("Characters: %d\n", cc);
    else if(op == 'w') 
      printf("Words: %d\n", wc);
    else if(op == 'l') 
      printf("Lines: %d\n", lc);
}

int main() {
    char input[100];
    char command[10], fn[50], op;

    while (1) {
        printf("myshell$ ");
        fgets(input, sizeof(input), stdin);

        input[strcspn(input, "\n")] = 0; 
        sscanf(input, "%s %c %s", command, &op, fn);

        if (strcmp(command, "count") == 0) {
            pid_t pid = fork();

            if(pid < 0) {
                printf("Fork failed.\n");
                return 1;
            } 
            else if(pid == 0) {
                count(fn, op);
                return 0;
            } 
            else {
                wait(NULL);
            }
        } else {
            printf("Invalid command. Use 'count <op> <filename>'\n");
        }
    }
    return 0;
}

