#include <stdio.h>
#include <stdlib.h>
#include <dirent.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/wait.h>
#include <string.h>

void list_files(const char *dirname, char option) {
    DIR *dp = opendir(dirname);
    if (dp == NULL) {
        perror("Failed to open directory");
        return;
    }

    struct dirent *entry;
    int dir_count = 0, file_count = 0;

    while ((entry = readdir(dp)) != NULL) {
        switch (option) {
            case 'f': // List filenames
                if (entry->d_type == DT_REG) {
                    printf("%s\n", entry->d_name);
                }
                break;
            case 'n': // Count entries
                if (entry->d_type == DT_DIR) dir_count++;
                if (entry->d_type == DT_REG) file_count++;
                break;
            case 'i': // List filenames with inode numbers
                if (entry->d_type == DT_REG) {
                    printf("%s\t%lu\n", entry->d_name, (unsigned long)entry->d_ino);
                }
                break;
            default:
                printf("Invalid option\n");
                closedir(dp);
                return;
        }
    }

    if (option == 'n') {
        printf("Total entries: %d Directory(s), %d File(s)\n", dir_count, file_count);
    }

    closedir(dp);
}

int main() {
    char input[100];

    while (1) {
        printf("myshell$ ");
        fflush(stdout);

        if (fgets(input, sizeof(input), stdin) == NULL) {
            break; // Exit on EOF or error
        }

        input[strcspn(input, "\n")] = 0; // Remove the newline character

        char *args[4]; // To hold command and its arguments
        char *token = strtok(input, " ");
        int arg_count = 0;

        while (token != NULL && arg_count < 4) {
            args[arg_count++] = token;
            token = strtok(NULL, " ");
        }
        args[arg_count] = NULL;

        // Check if the command is 'list'
        if (arg_count == 3 && strcmp(args[0], "list") == 0) {
            pid_t pid = fork();
            if (pid < 0) {
                perror("Fork failed");
            } else if (pid == 0) {
                // Child process
                list_files(args[2], args[1][0]);
                exit(0); // Exit child process
            } else {
                // Parent process
                wait(NULL); // Wait for child process to finish
            }
        } else {
            printf("Invalid command. Use: list [f|n|i] dirname\n");
        }
    }

    return 0;
}

