#include <stdio.h>
#include <unistd.h>
#include <fcntl.h>
#include<sys/wait.h>
#include <stdlib.h>
#include <string.h>

void typeline(char *option, char *filename) {
    int fh = open(filename, O_RDONLY);
    if (fh == -1) {
        printf("File %s not found.\n", filename);
        return;
    }

    char c;
    int n = 0, lines = 0;

    if (option[0] >= '0' && option[0] <= '9') {
        n = atoi(option);
        for (int i = 0; i < n; i++) {
            int line_count = 0;
            while (read(fh, &c, 1) > 0) {
                printf("%c", c);
                if (c == '\n') {
                    line_count++;
                    break;
                }
            }
            if (line_count == 0) break;
        }
    } 
    else if (strcmp(option, "-n") == 0) {
        while (read(fh, &c, 1) > 0) if (c == '\n') lines++;
        lseek(fh, 0, SEEK_SET); 

        int line_target = lines - n;
        int current_line = 0;
        while (read(fh, &c, 1) > 0) {
            if (c == '\n') current_line++;
            if (current_line >= line_target) printf("%c", c);
        }
    } 
    else if (strcmp(option, "a") == 0) {
        while (read(fh, &c, 1) > 0) printf("%c", c);
    } 
    else {
        printf("Invalid option: %s\n", option);
    }

    close(fh);
}

int main() {
    char command[80];

    while (1) {
        printf("myshell$ ");
        fgets(command, sizeof(command), stdin);
        command[strcspn(command, "\n")] = 0;

        if (strcmp(command, "exit") == 0) break;

        pid_t pid = fork();

        if (pid < 0) {
            printf("Fork failed.\n");
            return 1;
        } else if (pid == 0) {
            char *args[4]; 
            args[0] = strtok(command, " ");
            args[1] = strtok(NULL, " ");
            args[2] = strtok(NULL, " ");
            args[3] = NULL; 

            if (args[0] != NULL && strcmp(args[0], "typeline") == 0 && args[1] != NULL && args[2] != NULL) {
                typeline(args[1], args[2]);
            } else {
                execvp(args[0], args);
                printf("Command not found.\n");
            }
            exit(0); 
        } else {
            wait(NULL);
        }
    }

    return 0;
}

