switch (op) {
        case 'f': // Find first occurrence
            while (read(fh, &c, 1)) {
                buff[j++] = c;
                if (c == '\n') {
                    buff[j] = '\0';
                    j = 0;
                    i++;
                    if (strstr(buff, pattern)) {
                        printf("%d: %s\n", i, buff);
                        break; // Exit after finding the first occurrence
                    }
                }
            }
            break;

        case 'c': // Count occurrences
            while (read(fh, &c, 1)) {
                buff[j++] = c;
                if (c == '\n') {
                    buff[j] = '\0';
                    j = 0;
                    p = buff;
                    while ((p = strstr(p, pattern))) {
                        count++;
                        p++; // Move to the next character after the found pattern
                    }
                }
            }
            printf("Total No. of Occurrences = %d\n", count);
            break;

        case 'a': // List all occurrences
            while (read(fh, &c, 1)) {
                buff[j++] = c;
                if (c == '\n') {
                    buff[j] = '\0';
                    j = 0;
                    i++;
                    if (strstr(buff, pattern)) {
                        printf("%d: %s\n", i, buff);
                    }
                }
            }
            break;

        default:
            printf("Invalid operation\n");
            break;
    }
    close(fh);
}

int main() {
    char buff[80], *args[10];
    int pid;

    while (1) {
        printf("myshell$ ");
        fflush(stdout);

        if (fgets(buff, 80, stdin) == NULL) {
            perror("Failed to read command");
            continue;
        }
        buff[strcspn(buff, "\n")] = '\0'; // Remove newline character

        make_toks(buff, args);
        if (strcmp(args[0], "search") == 0) {
            if (args[1] && args[2] && args[3]) {
                search(args[2], args[1][0], args[3]);
            } else {
                printf("Usage: search [f|a|c] filename pattern\n");
            }
        } else {
            pid = fork();
            if (pid > 0) {
                wait(NULL); // Parent waits for child
            } else if (pid == 0) {
                if (execvp(args[0], args) == -1) {
                    printf("Bad command.\n");
                    exit(EXIT_FAILURE);
                }
            } else {
                perror("Fork failed");
            }
        }
    }
    return 0;
}

