// LFU (Least Frequently Used)
#include <stdio.h>

int main() {
    int pc, fc, pf = 0, ph = 0;
    int i, j, k, found;

    printf("Enter the count of Pages : ");
    scanf("%d", &pc);
    int p[pc];

    printf("Enter the page reference string : ");
    for (i = 0; i < pc; i++) {
        scanf("%d", &p[i]);
    }

    printf("Enter Frame count : ");
    scanf("%d", &fc);
    int f[fc], freq[fc];

    for (i = 0; i < fc; i++) {
        f[i] = -1;
        freq[i] = 0;
    }

    for (i = 0; i < pc; i++) {
        found = 0;
        for (j = 0; j < fc; j++) {
            if (f[j] == p[i]) {
                found = 1;
                freq[j]++;
                ph++;
                break;
            }
        }
        if (!found) {
            int min_index = 0;
            for (j = 1; j < fc; j++) {
                if (freq[j] < freq[min_index]) {
                    min_index = j;
                }
            }
            f[min_index] = p[i];
            freq[min_index] = 1;
            pf++;
        }

        printf("Page %d -> Frames: ", p[i]);
        for (j = 0; j < fc; j++) {
            if (f[j] == -1) {
                printf("- ");
            } else {
                printf("%d ", f[j]);
            }
        }
        printf("\n");
    }

    printf("Total page faults: %d\n", pf);
    printf("Total hits: %d\n", ph);

    return 0;
}

